const fs = require('fs');const path = require('path');const ts = require('typescript');const screenshotDataPath = path.join(__dirname, 'screenshot_data.ts');const fileContents = fs.readFileSync(screenshotDataPath, 'utf8');const sourceFile = ts.createSourceFile('screenshot_data.ts', fileContents, ts.ScriptTarget.Latest, true);let screenshotData;ts.forEachChild(sourceFile, node => {  if (ts.isVariableStatement(node)) {    node.declarationList.declarations.forEach(declaration => {      if (declaration.name.getText(sourceFile) === 'screenshotData') {        const initializer = declaration.initializer;        if (initializer) {          screenshotData = eval(`(${initializer.getText(sourceFile)})`);        }      }    });  }});if (!screenshotData) {  console.error('Could not extract screenshotData from screenshot_data.ts');  process.exit(1);}screenshotData.forEach(item => {  if (!item.setupFunction) {
    return;
  }  item.deviceType.forEach(device => {    let imageName;    if (typeof item.imageName === 'string') {      imageName = item.imageName;    } else {      imageName = item.imageName[device];    }    if (imageName) {      const description = Array.isArray(item.description) ? item.description.join('_NEWLINE_') : item.description;      console.log(`${imageName}	${item.testId}	${device}	"${description}"`);    }  });});